def words_string(s):
    import re
    return [word for word in re.split('[ ,]+', s.strip()) if word]

def choose_num(x, y):
    if x > y:
        return -1
    if y % 2 == 0:
        return y
    elif y - 1 >= x:
        return y - 1
    else:
        return -1

def rounded_avg(n, m):
    if n > m:
        return -1
    avg = round((n + m) / 2)
    return bin(avg)

def unique_digits(x):
    res = []
    for num in x:
        digits = str(num)
        if all(int(d) % 2 != 0 for d in digits):
            res.append(num)
    return sorted(res)

def f(n):
    res = []
    for i in range(1, n + 1):
        if i % 2 == 0:
            fact = 1
            for j in range(1, i + 1):
                fact *= j
            res.append(fact)
        else:
            res.append(sum(range(1, i + 1)))
    return res

def even_odd_palindrome(n):
    evens = 0
    odds = 0
    for i in range(1, n + 1):
        if str(i) == str(i)[::-1]:
            if i % 2 == 0:
                evens += 1
            else:
                odds += 1
    return (evens, odds)

def count_nums(arr):
    def digit_sum(num):
        s = sum(int(d) for d in str(abs(num)))
        return s if num >= 0 else s - 2 * int(str(abs(num))[0])
    return sum(1 for x in arr if digit_sum(x) > 0)

def move_one_ball(arr):
    if not arr:
        return True
    sorted_arr = sorted(arr)
    for i in range(len(arr)):
        if arr[i:] + arr[:i] == sorted_arr:
            return True
    return False
