def make_a_pile(n):
    pile = []
    current = n
    for _ in range(n):
        pile.append(current)
        if n % 2 == 0:
            current += 2
        else:
            current += 2
    return pile

def choose_num(x, y):
    if x > y:
        return -1
    if y % 2 == 0:
        return y
    else:
        return y - 1 if y - 1 >= x else -1

def rounded_avg(n, m):
    if n > m:
        return -1
    average = (n + m) / 2
    rounded = round(average)
    return bin(rounded)

def by_length(arr):
    digits = {"One":1,"Two":2,"Three":3,"Four":4,"Five":5,"Six":6,"Seven":7,"Eight":8,"Nine":9}
    sorted_arr = sorted([x for x in arr if 1 <= x <= 9], reverse=True)
    return [k for v in sorted_arr for k, val in digits.items() if val == v]

def f(n):
    def factorial(x):
        return 1 if x == 0 else x * factorial(x-1)
    result = []
    for i in range(1, n+1):
        if i % 2 == 0:
            result.append(factorial(i))
        else:
            result.append(sum(range(1, i+1)))
    return result

def even_odd_palindrome(n):
    def is_palindrome(x):
        return str(x) == str(x)[::-1]
    even = 0
    odd = 0
    for i in range(1, n+1):
        if is_palindrome(i):
            if i % 2 == 0:
                even += 1
            else:
                odd += 1
    return (even, odd)

def move_one_ball(arr):
    if not arr:
        return True
    n = len(arr)
    for i in range(n):
        if arr[i:] + arr[:i] == sorted(arr):
            return True
    return False
