def make_a_pile(n):
    stones = []
    for i in range(n):
        if n % 2 == 0:
            stones.append(n + 2 * i)
        else:
            stones.append(n + 2 * i + 1)
    return stones

def words_string(s):
    import re
    return [word.strip() for word in re.split(r'[,\s]+', s) if word.strip()]

def choose_num(x, y):
    for num in range(min(x, y), max(x, y) + 1)[::-1]:
        if num % 2 == 0:
            return num
    return -1

def rounded_avg(n, m):
    if n > m:
        return -1
    avg = round((n + m) / 2)
    return bin(avg)

def by_length(arr):
    names = ["", "One", "Two", "Three", "Four", "Five", "Six", "Seven", "Eight", "Nine"]
    return [names[i] for i in sorted([x for x in arr if 1 <= x <= 9], reverse=True)]

def f(n):
    results = []
    for i in range(1, n + 1):
        if i % 2 == 0:
            factorial = 1
            for j in range(1, i + 1):
                factorial *= j
            results.append(factorial)
        else:
            results.append(sum(range(1, i + 1)))
    return results

def even_odd_palindrome(n):
    even_count = 0
    odd_count = 0
    for i in range(1, n + 1):
        if str(i) == str(i)[::-1]:
            if i % 2 == 0:
                even_count += 1
            else:
                odd_count += 1
    return (even_count, odd_count)

def count_nums(arr):
    counter = 0
    for num in arr:
        digit_sum = sum(int(d) if num >= 0 else -int(d) for d in str(abs(num)))
        if digit_sum > 0:
            counter += 1
    return counter

def move_one_ball(arr):
    if not arr:
        return True
    drops = 0
    n = len(arr)
    for i in range(n):
        if arr[i] > arr[(i + 1) % n]:
            drops += 1
    return drops <= 1
