def choose_num(x, y):
    if x > y:
        return -1
    for num in range(y, x - 1, -1):
        if num % 2 == 0:
            return num
    return -1

def rounded_avg(n, m):
    if n > m:
        return -1
    total = sum(range(n, m + 1))
    count = m - n + 1
    avg = total / count
    return bin(round(avg))

def f(n):
    from math import factorial
    return [factorial(i) if i % 2 == 0 else sum(range(1, i + 1)) for i in range(1, n + 1)]

def even_odd_palindrome(n):
    even_count = 0
    odd_count = 0
    for i in range(1, n + 1):
        if str(i) == str(i)[::-1]:
            if i % 2 == 0:
                even_count += 1
            else:
                odd_count += 1
    return (even_count, odd_count)

def move_one_ball(arr):
    if not arr:
        return True
    n = len(arr)
    count = 0
    for i in range(n):
        if arr[i] > arr[(i + 1) % n]:
            count += 1
    return count <= 1
